          "sentence": null,
          "note": null,
          "path": "epubcfi(/6/54[id601]!/4[6LJU0-1f358c62d1fd4a3ab884eca79514436e]/2/12/1,:52,:68)",
          "created_at": "2022-08-08 21:47:32 -0700",
