          "book_id": "1784C55E9361272CB698DFC3676CD445",
          "chapter": "20",
          "text": "Tio Tico!",
          "sentence": null,
          "note": null,
          "path": "epubcfi(/6/52[id16]!/4/2[vivapovobrasileiro-epub]/2/234/1,:191,:200)",
          "created_at": "2022-11-19 15:15:22 -0800",
