          "book_id": "B4CEE1EB49219A027623F448D22FB556",
          "chapter": "Final",
          "text": "Giacomino da Verona",
          "sentence": null,
          "note": null,
          "path": "epubcfi(/6/20[id24]!/4[FRANCISCO_DE_ASSIS_Editado-9]/2/14/3,:19,:38)",
          "created_at": "2022-05-02 21:41:13 -0700",
