      "title": "Cartas a um jovem poeta",
      "last_engaged_date": "2022-11-28 00:18:29 -0800",
      "reading_progress": 0.0,
      "marked_as_finished": 1,
      "purchase_date": "2023-01-18 09:29:40 -0800",
      "genre": null,
      "lang": null,
      "file_size": 312130,
      "page_count": 0,
      "created_at": "2023-01-18 09:29:45 -0800",
